import React, { useState, useEffect } from "react";
import { Article } from "./Article";
import axios from "axios";

import "./Base.css";

export const Base = () => {
  const [wordSearch, setWordSearch] = useState();
  const [data, setData] = useState([]);
  // useEffect(() => {
  //   async function getData() {
  //     const actualData = await fetch(
  //       "https://www.alphavantage.co/query?function=NEWS_SENTIMENT&tickers=AAPL&apikey=3UMZBONPRJC6CCXF"
  //     ).then((response) => response.json());

  //     console.log(actualData);
  //     setData([...actualData.feed]);
  //   }
  //   getData();
  // }, []);
  const handleSearch = () => {
    axios
      .get(
        `https://www.alphavantage.co/query?function=NEWS_SENTIMENT&tickers=${wordSearch}&apikey=3UMZBONPRJC6CCXF`
      )
      .then((res) => {
        console.log(...res);
        setData([...res.feed]);
      });
  };

  return (
    <div>
      <nav class="navbar navbar-light bg-light">
        <form class="form-inline">
          <input
            class="form-control mr-sm-2"
            type="search"
            placeholder="Search"
            aria-label="Search"
            onChange={(e) => setWordSearch(e.target.value)}
          ></input>
          <button
            class="btn btn-outline-success my-2 my-sm-0"
            type="submit"
            onClick={handleSearch}
          >
            Search
          </button>
        </form>
      </nav>
      <h1>heloo</h1>

      {data.map((item) => (
        <Article item={item} />
      ))}
    </div>
  );
};
export default Base;
